	/**
	 * Write logic for dynamic css change for the elements
	 *
	 * @return array dynamic styles
	 */
	public function dynamic_styles() {

		$logo_default = \Rishi\Customizer\Helpers\Defaults::get_header_defaults();
		$logoMaxWidth = $this->get_mod_value('logoMaxWidth', $logo_default['logoMaxWidth']);
		$logo_margin = $this->get_mod_value('headerLogoMargin', $logo_default['headerLogoMargin']);
		$title_color = $this->get_mod_value('title_color_group', [
			'siteTitleColor' => $logo_default['siteTitleColor'],
		]);
		$tagline_color = $this->get_mod_value('tagline_color_group', [
			'siteTaglineColor' => $logo_default['siteTaglineColor']
		]);
		$siteTitleColor = $title_color['siteTitleColor'];
		$siteTaglineColor = $tagline_color['siteTaglineColor'];
		$dateTitleFont = $this->get_mod_value('siteTitle', $logo_default['siteTitle']);
		$dateTaglineFont = $this->get_mod_value('siteTagline', $logo_default['siteTagline']);

		$options = array(
			'logoMaxWidth' => array(
				'selector' => '.site-branding',
				'variableName' => 'LogoMaxWidth',
				'value' => $logoMaxWidth,
				'responsive' => true,
				'type' => 'slider'
			),
			'headerLogoMargin' => array(
				'selector' => '.site-branding',
				'value' => $logo_margin,
				'type' => 'spacing',
				'responsive' => true,
				'important' => true,
				'variableName' => 'margin',
				'property' => 'margin',
				'unit' => 'px',
			),
			'siteTitleColor' => array(
				'value' => $siteTitleColor,
				'default' => $logo_default['siteTitleColor'],
				'variables' => array(
					'default' => array(
						'variable' => 'linkInitialColor',
						'selector' => '.site-branding',
					),
					'hover' => array(
						'variable' => 'linkHoverColor',
						'selector' => '.site-branding',
					),
				),
				'type' => 'color',
			),
			'siteTaglineColor' => array(
				'value' => $siteTaglineColor,
				'default' => $logo_default['siteTaglineColor'],
				'variables' => array(
					'default' => array(
						'selector' => '.site-branding',
						'variable' => 'color',
					),
				),
				'type' => 'color',
			),
			'siteTitle' => array(
				'value' => $dateTitleFont,
				'selector' => '.site-header #logo .site-title',
				'type' => 'typography'
			),
			'siteTagline' => array(
				'value' => $dateTaglineFont,
				'selector' => '.site-header #logo .site-description',
				'type' => 'typography'
			),
		);

		return apply_filters(
			'dynamic_header_element_'.$this->get_id().'_options',
			$options,
			$this
		);
	}

	/**
	 * Add markup for the element
	 * @return void
	 */
	public function render( $device = 'desktop') {

		$logo_type = $this->get_mod_value('logo_type', 'logo-title');
		$logo_title_layout = $this->get_mod_value('logo_title_layout', 'logotitle');
		$logo_title_tagline_layout = $this->get_mod_value('logo_title_tagline_layout', 'logotitletagline');
		$has_site_title = $this->get_mod_value('has_site_title', 'yes');
		$has_tagline = $this->get_mod_value('has_tagline', 'no');
		$blogname = $this->get_mod_value('blogname', get_option('blogname'));
		$blogdescription = $this->get_mod_value('blogdescription', get_option('blogdescription'));
		$blogname_visibility = $this->get_mod_value(
			'blogname_visibility',
			array(
				'desktop' => 'desktop',
				'tablet' => 'tablet',
				'mobile' => 'mobile',
			)
		);
		$blogdescription_visibility = $this->get_mod_value(
			'blogdescription_visibility',
			array(
				'desktop' => 'desktop',
				'tablet' => 'tablet',
				'mobile' => 'mobile',
			)
		);

		$blogname_class = $this->get_visible_device_class($blogname_visibility);
		$blogdesc_class = $this->get_visible_device_class($blogdescription_visibility);

		$rishi_tag = 'span';

		if ( ! wp_is_mobile() && is_front_page() && $device ==='desktop') {
			$rishi_tag = 'h1';
		}

		switch( $logo_type ) {
			case 'logo':
				$blogname_class .= ' screen-reader-text site-title';
				$description = '';
				$logo_title_layout_class = '';
				break;

			case 'logo-title':
				$blogname_class .= ' site-title';
				$description = '';
				$logo_title_layout_class = $logo_title_layout;
				break;

			case 'logo-title-tagline':
				$blogname_class .= ' site-title';
				$description = '<p class="site-description '.$blogdesc_class.'" itemprop="description">'.$blogdescription.'</p>';
				$logo_title_layout_class = $logo_title_tagline_layout;
				break;

			default:
				$blogname_class .= ' site-title';
				$description = '';
				$logo_title_layout_class = '';
				break;
		}
		?>
		<div class="site-branding <?php echo esc_attr($logo_title_layout_class); ?>" id="logo" <?php echo rishi_print_schema('logo'); ?>>
			<div class="site-branding-container">
				<?php
				$ed_custom = false;
				$custom_logo = $this->get_mod_value('custom_logo');
				$ed_transparent = false;
				$transparent = get_theme_mod('transparent_logo');
				$ed_sticky = false;
				$sticky = get_theme_mod('sticky_logo');

				if(get_theme_mod('has_transparent_header') === 'yes') {
					if(!empty($transparent) && is_array($transparent) && isset($transparent['_value']['attachment_id'])) {
						$transparent_id = $transparent['_value']['attachment_id'];
						$ed_transparent = true;
					}
				}

				if(get_theme_mod('has_sticky_header') === 'yes') {
					if(!empty($sticky) && is_array($sticky) && isset($sticky['_value']['attachment_id'])) {
						$sticky_id = $sticky['_value']['attachment_id'];
						$ed_sticky = true;
					}
				}

				if(!empty($custom_logo) && is_array($custom_logo) && isset($custom_logo['_value']['attachment_id'])) {
					$attachment_id = $custom_logo['_value']['attachment_id'];
					$ed_custom = true;
				}

				if($ed_custom || $ed_sticky || $ed_transparent) {
					echo '<a class="site-logo" href="'.esc_url(home_url()).'" rel="home" itemprop="url">';
					if($ed_custom)
						echo wp_get_attachment_image($attachment_id, 'full', false, ['data-logo' => 'default']);
					if($ed_sticky)
						echo wp_get_attachment_image($sticky_id, 'full', false, ['data-logo' => 'sticky']);
					if($ed_transparent)
						echo wp_get_attachment_image($transparent_id, 'full', false, ['data-logo' => 'transparent']);
					echo '</a>';
				}

				if ( $logo_title_tagline_layout !== 'titlelogotagline' ) {
					echo '<div class="site-title-wrapper">';
				}
				if ( $has_site_title == 'yes' ) {
					?>
					<<?php echo $rishi_tag; ?> class="<?php echo esc_attr( $blogname_class ); ?>" itemprop="name">
						<a href="<?php echo esc_url( home_url() ); ?>" rel="home">
							<?php echo esc_html($blogname); ?>
						</a>
					</<?php echo $rishi_tag; ?>>
					<?php
				}
				if($has_tagline == 'yes') {
					echo $description;
				}
				if ( $logo_title_tagline_layout !== 'titlelogotagline' ){
					echo '</div>';
				}
					?>
				</div>
			</div>
		<?php
	}
}
